import React from 'react';

function Home({ onNavigate, isLoggedIn }) {
  return (
    <div style={{ textAlign: 'center', marginTop: 60 }}>
      <h1 style={{ color: '#1976d2', fontWeight: 700 }}>Welcome to CFO Helper Agent</h1>
      <p style={{ fontSize: 18, margin: '20px 0' }}>
        Simulate budget scenarios, forecast outcomes, and manage finances with ease.<br />
        <span style={{ color: '#43a047', fontWeight: 500 }}>Your virtual CFO for startups, events, and small businesses.</span>
      </p>
      <img src="https://img.icons8.com/ios-filled/100/finance.png" alt="Finance" style={{ margin: 20 }} />
      <div style={{ margin: '30px auto', maxWidth: 500, background: '#f9f9f9', borderRadius: 10, boxShadow: '0 0 10px #eee', padding: 20 }}>
        <h3 style={{ color: '#1976d2' }}>Features</h3>
        <ul style={{ textAlign: 'left', fontSize: 16, margin: '20px auto', maxWidth: 400 }}>
          <li>Adjust spending, hiring, and pricing with sliders</li>
          <li>See forecast in text/chart</li>
          <li>Export/share short report</li>
          <li>Usage counter (scenarios tested)</li>
          <li>Flexprice integration: bill per scenario/report</li>
          <li>Pathway integration: live financial data updates</li>
        </ul>
        {!isLoggedIn && (
          <div style={{ marginTop: 20 }}>
            <button onClick={() => onNavigate('login')} style={{ padding: '10px 20px', background: '#1976d2', color: '#fff', border: 'none', borderRadius: 4, marginRight: 10 }}>Login</button>
            <button onClick={() => onNavigate('signup')} style={{ padding: '10px 20px', background: '#43a047', color: '#fff', border: 'none', borderRadius: 4 }}>Sign Up</button>
          </div>
        )}
        {isLoggedIn && (
          <div style={{ marginTop: 20, color: '#43a047', fontWeight: 500 }}>
            You are logged in! Start simulating scenarios below.
          </div>
        )}
      </div>
    </div>
  );
}

export default Home;
