import React, { useState, useEffect } from 'react';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);
import Home from './Home';
import Login from './Login';
import Signup from './Signup';
import Navbar from './Navbar';


function App() {
  const [engineers, setEngineers] = useState(5);
  const [marketing, setMarketing] = useState(20000);
  const [productPrice, setProductPrice] = useState(100);
  const [forecast, setForecast] = useState(null);
  const [usageCount, setUsageCount] = useState(0);
  const [reportUrl, setReportUrl] = useState('');
  const [page, setPage] = useState('home');
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch('http://localhost:4000/api/usage')
      .then(res => res.json())
      .then(data => setUsageCount(data.usageCount));
  }, []);

  const simulateScenario = async () => {
    const res = await fetch('http://localhost:4000/api/simulate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ engineers, marketing, productPrice })
    });
    const data = await res.json();
    setForecast(data.forecast);
    setUsageCount(data.usageCount);
  };

  const exportReport = async () => {
    const res = await fetch('http://localhost:4000/api/export', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' }
    });
    const data = await res.json();
    setReportUrl(data.reportUrl);
    setUsageCount(data.usageCount);
  };

  const handleLogin = (email) => {
    setUser(email);
    setPage('home');
  };
  const handleSignup = (email) => {
    setUser(email);
    setPage('home');
  };
  const handleLogout = () => {
    setUser(null);
    setPage('login');
  };

  let content;
  if (page === 'login') {
    content = <Login onLogin={handleLogin} />;
  } else if (page === 'signup') {
    content = <Signup onSignup={handleSignup} />;
  } else if (page === 'home') {
    content = (
      <>
        <Home />
        {user && (
          <div style={{ maxWidth: 600, margin: '30px auto', padding: 20, background: '#f9f9f9', borderRadius: 8, boxShadow: '0 0 10px #eee' }}>
            <h2 style={{ color: '#1976d2' }}>Scenario Simulator</h2>
            <div style={{ marginBottom: 15 }}>
              <label>Engineers: <b>{engineers}</b></label>
              <input type="range" min="1" max="20" value={engineers} onChange={e => setEngineers(Number(e.target.value))} style={{ width: '100%' }} />
            </div>
            <div style={{ marginBottom: 15 }}>
              <label>Marketing Spend: <b>₹{marketing}</b></label>
              <input type="range" min="0" max="100000" step="1000" value={marketing} onChange={e => setMarketing(Number(e.target.value))} style={{ width: '100%' }} />
            </div>
            <div style={{ marginBottom: 15 }}>
              <label>Product Price: <b>₹{productPrice}</b></label>
              <input type="range" min="10" max="1000" step="10" value={productPrice} onChange={e => setProductPrice(Number(e.target.value))} style={{ width: '100%' }} />
            </div>
            <button onClick={simulateScenario} style={{ padding: '10px 20px', background: '#1976d2', color: '#fff', border: 'none', borderRadius: 4 }}>Simulate Scenario</button>
            <button onClick={exportReport} style={{ marginLeft: 10, padding: '10px 20px', background: '#43a047', color: '#fff', border: 'none', borderRadius: 4 }}>Export Report</button>
            <div style={{ marginTop: 20 }}>
              <strong>Usage Count:</strong> {usageCount}
            </div>
            {forecast && (
              <div style={{ marginTop: 20, background: '#fff', padding: 15, borderRadius: 6, boxShadow: '0 0 5px #ddd' }}>
                <h3 style={{ color: '#1976d2' }}>Forecast</h3>
                <div>Engineers: {forecast.engineers}</div>
                <div>Marketing: ₹{forecast.marketing}</div>
                <div>Product Price: ₹{forecast.productPrice}</div>
                <div>Expenses: ₹{forecast.expenses}</div>
                <div>Revenue: ₹{forecast.revenue}</div>
                <div>Profit: ₹{forecast.profit}</div>
              </div>
            )}
            {reportUrl && (
              <div style={{ marginTop: 20 }}>
                <a href={reportUrl} target="_blank" rel="noopener noreferrer">Download Report</a>
              </div>
            )}
          </div>
        )}
      </>
    );
  }

  return (
    <div style={{ background: '#f5f5f5', minHeight: '100vh' }}>
      <Navbar onNavigate={setPage} isLoggedIn={!!user} onLogout={handleLogout} />
      {content}
    </div>
  );
}

export default App;
